import React from 'react'
import { useHistory } from "react-router-dom";
import { useForm, Form } from '../../hooks/useForm';

const FormContact = (props) => {
    const { Contact } = props;

    const validate = (fieldValues = values) => {
        let temp = { ...errors }
        if ('username' in fieldValues)
            temp.username = fieldValues.username ? "" : "This field is required."
        if ('password' in fieldValues)
            temp.password = fieldValues.password ? "" : "This field is required."
        setErrors({
            ...temp
        })
        if (fieldValues == values)
            return Object.values(temp).every(x => x == "")
    }

    const {
        values, setValues,
        errors, setErrors,
        handleInputChange,
        resetForm
    } = useForm(initialFValues, true, validate);

    const handleSubmitUser = e => {
        e.preventDefault()
        if (validate()) {
            const FormLogin = new FormData()
            FormLogin.append('username', values.username)
            FormLogin.append('password', values.password)
            UserLogin(FormLogin, resetForm);
            history.push("/")
        }
    }

    return (
        <form action="#" method="post" encType="multipart/form-data">
            <div className="form-fields">
                <div className="form-group row">
                    <div className="col-md-6">
                        <input className="form-control" name="name" placeholder="Your name" />
                    </div>
                    <div className="col-md-6 margin-bottom-mobie">
                        <input className="form-control" name="from" type="email" defaultValue placeholder="Your email" />
                    </div>
                </div>
                <div className="form-group row">
                    <div className="col-md-12 margin-bottom-mobie">
                        <input className="form-control" name="from" type="email" defaultValue placeholder="Subject" />
                    </div>
                </div>
                <div className="form-group row">
                    <div className="col-md-12">
                        <textarea className="form-control" name="message" placeholder="Message" rows={8} defaultValue={""} />
                    </div>
                </div>
            </div>
            <div>
                <button className="btn" type="submit" name="submitMessage">
                    <img className="img-fl" src="img/other/contact_email.png" alt="img" />Send message
                </button>
            </div>
        </form>
    );
};

export default FormContact;
