import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';

const DiscountProduct = () => {

    function formatNumber(number) {
        var numString = number.toString()
        var result = ''
        while (numString.length > 3) {
            var chunk = numString.substr(-3)
            numString = numString.substr(0, numString.length - 3)
            result = ',' + chunk + result
        }

        if (numString.length > 0) {
            result = numString + result
        }

        return result
    }

    const discount = useSelector((state) => state.allDiscount.discount);

    const renderList = discount.map((discount) => {
        const {
            id, tenSP, giaSP, urlAnhSanPham, soluong, giaKhuyenMai, khuyenMaiNavigation, danhMucHinhNavigation,
        } = discount;
        return (
            <div className="item text-center" style={{ maxWidth: '430px' }} key={id}>
                <Link to={`/DetailProduct/${id}`}>
                    <div className="product-miniature js-product-miniature item-one first-item"
                        style={{
                            width: '350px'
                        }}>
                        <div className="thumbnail-container" style={{ border: '1px solid #dee2e6', height: '350px' }}>
                            <a href="#">
                                <img className="img-fluid image-cover" style={{ width: '350px', height: '350px', objectFit: 'contain' }} src={urlAnhSanPham} alt="img" />
                                <img className="img-fluid image-secondary" src={danhMucHinhNavigation[0].urlAnh} style={{ width: '350px', height: '350px', objectFit: 'contain' }} alt="img" />
                            </a>
                            <div class="product-flags discount">-{khuyenMaiNavigation.sale}%</div>
                            <div className="highlighted-informations">
                                <div className="variant-links">
                                    <a href="#" className="color beige" title="Beige" />
                                    <a href="#" className="color orange" title="Orange" />
                                    <a href="#" className="color green" title="Green" />
                                </div>
                            </div>
                        </div>
                        <div className="product-description">
                            <div className="product-groups">
                                <div className="product-title"
                                    style={{ fontSize: '20px' }}
                                >
                                    <a href="product-detail.html">{tenSP}</a>
                                </div>
                                <div className="rating">
                                    <div class="star-content" style={{ marginTop: '6px', color: 'green', fontSize: '15px' }}>
                                        <span className="info-stock">
                                            <i className="fa fa-check-square-o" aria-hidden="true" />
                                            {
                                                soluong > 0 ? "Còn hàng" : "Hết hàng"
                                            }
                                        </span>
                                    </div>
                                </div>
                                <div className="product-group-price">
                                    <div className="product-price-and-shipping">
                                        {
                                            khuyenMaiNavigation.sale > 0 ?
                                                <>
                                                    <span
                                                        style={{ fontSize: '18px' }}
                                                        className="price">{formatNumber(giaKhuyenMai)} vnđ</span>
                                                    <del
                                                        style={{ fontSize: '18px' }}
                                                        class="regular-price">{formatNumber(giaSP)} vnđ</del>
                                                </>
                                                : <span
                                                    style={{ fontSize: '18px' }}
                                                    className="price">{formatNumber(giaSP)} vnđ</span>
                                        }

                                    </div>
                                </div>
                            </div>

                            <div className="product-buttons d-flex justify-content-center">
                                <form action="#" method="post" className="formAddToCart">
                                    <input type="hidden" name="id_product" defaultValue={1} />
                                    <a
                                        style={{ width: '50px', height: '50px' }}
                                        className="add-to-cart" href="#" data-button-action="add-to-cart">
                                        <i
                                            style={{ lineHeight: '50px' }}
                                            className="fa fa-shopping-cart" aria-hidden="true" />
                                    </a>
                                </form>
                                {/* <a className="addToWishlist" href="#" data-rel={1} onclick>
                            <i className="fa fa-heart" aria-hidden="true" />
                        </a> */}
                                <Link to={`/DetailProduct/${id}`}
                                    style={{ width: '50px', height: '50px' }}
                                    className="quick-view hidden-sm-down" data-link-action="quickview">
                                    <i
                                        style={{ lineHeight: '50px' }}
                                        className="fa fa-eye" aria-hidden="true" />
                                </Link>
                            </div>
                        </div>
                    </div>
                </Link>
            </div>
        );
    })

    return (
        <>
            {
                discount.length > 0 ?
                    <OwlCarousel className='owl-carousel owl-theme' loop margin={10} nav>
                        {renderList}
                    </OwlCarousel>
                    : <h1>...Loading</h1>
            }
        </>
    );
};

export default DiscountProduct;
