import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import { setRelatedProduct } from '../../redux/actions/productActions'
import { Link } from 'react-router-dom';

const BaseURL = "https://localhost:44336/api/Products/GetRelatedProduct/"
const RelatedProducts = () => {

    function formatNumber(number) {
        var numString = number.toString()
        var result = ''
        while (numString.length > 3) {
            var chunk = numString.substr(-3)
            numString = numString.substr(0, numString.length - 3)
            result = ',' + chunk + result
        }

        if (numString.length > 0) {
            result = numString + result
        }

        return result
    }

    const Relatedproduct = useSelector((state) => state.relatedProduct.danhMucSanPham)

    const { id } = useParams();

    const dispatch = useDispatch();

    const fetchRelatedProduct = async () => {
        const response = await axios
            .get(BaseURL + id)
            .catch((err) => console.log(err));
        dispatch(setRelatedProduct(response.data));
    }

    useEffect(() => {
        if (id && id !== '') {
            fetchRelatedProduct();
        }
    }, [id]);
    console.log(Relatedproduct)

    const render = Relatedproduct.map((item) => {
        console.log(item)
        return (
            <div className="item text-center col-md-4" keys={item.id}>
                <Link to={`/DetailProduct/${id}`}>
                    <div className="product-miniature js-product-miniature item-one first-item">
                        <div className="thumbnail-container border border">

                            <a href="#">
                                <img className="img-fluid image-cover" style={{ width: '280.88px', height: '260px', objectFit: 'contain' }} src={item.urlAnhSanPham} alt="img" />
                                <img className="img-fluid image-secondary" src={item.urlAnhSanPham} style={{ width: '280.88px', height: '260px', objectFit: 'contain' }} alt="img" />
                            </a>
                            {
                                item.khuyenMaiNavigation.sale > 0 ?
                                    <div class="product-flags discount">-{item.khuyenMaiNavigation.sale}%</div>
                                    : ""
                            }
                            <div className="highlighted-informations">
                                <div className="variant-links">
                                    <a href="#" className="color beige" title="Beige" />
                                    <a href="#" className="color orange" title="Orange" />
                                    <a href="#" className="color green" title="Green" />
                                </div>
                            </div>
                        </div>
                        <div className="product-description">
                            <div className="product-groups">
                                <div className="product-title">
                                    <Link to={`/DetailProduct/${id}`}>{item.tenSP}</Link>
                                </div>
                                <div className="rating">
                                    <div class="star-content" style={{ marginTop: '6px', color: 'green', fontSize: '15px' }}>
                                        <span className="info-stock">
                                            <i className="fa fa-check-square-o" aria-hidden="true" />
                                            {
                                                item.soluong > 0 ? "Còn hàng" : "Hết hàng"
                                            }
                                        </span>
                                    </div>
                                </div>
                                <div className="product-group-price">
                                    <div className="product-price-and-shipping">
                                        {
                                            item.khuyenMaiNavigation.sale > 0 ?
                                                <>
                                                    <span className="price">{formatNumber(item.giaKhuyenMai)} vnđ</span>
                                                    <del class="regular-price">{formatNumber(item.giaSP)} vnđ</del>
                                                </>
                                                : <span className="price">{formatNumber(item.giaSP)} vnđ</span>
                                        }
                                    </div>
                                </div>
                            </div>
                            <div className="product-buttons d-flex justify-content-center">
                                <form action="#" method="post" className="formAddToCart">
                                    <a className="add-to-cart" href="#" data-button-action="add-to-cart">
                                        <i className="fa fa-shopping-cart" aria-hidden="true" />
                                    </a>
                                </form>
                                {/* <a className="addToWishlist" href="#" data-rel={1} onclick>
                            <i className="fa fa-heart" aria-hidden="true" />
                        </a> */}
                                <Link to={`/DetailProduct/${id}`} className="quick-view hidden-sm-down" data-link-action="quickview">
                                    <i className="fa fa-eye" aria-hidden="true" />
                                </Link>
                            </div>
                        </div>
                    </div>
                </Link>

            </div>
        );
    })

    return (
        <div className="related">
            <div className="title-tab-content  text-center">
                <div className="title-product justify-content-start">
                    <h2>Sản phẩm liên quan</h2>
                </div>
            </div>
            <div className="tab-content">
                <div className="row">
                    {render}
                </div>
            </div>
        </div>
    );
};

export default RelatedProducts;
