import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import NewProduct from './NewProduct';
import { setProducts } from '../../redux/actions/productActions';
import { setCategorys } from '../../redux/actions/categoryActions';
import ListDanhmuc from './ListDanhmuc';
import Baner from './Baner';
import Policy from './Policy';

function Home() {
	const product = useSelector((state) => state);

	const dispatch = useDispatch();

	const fetchProducts = async () => {
		const response = await axios.get("https://localhost:44336/api/Home/SanPhamMoiNhat")
			.catch((err) => console.log(err));
		dispatch(setProducts(response.data));
	};

	const fetchCategory = async () => {
		const response = await axios.get("https://localhost:44336/api/Home/ListDanhMucSp")
			.catch((err) => console.log(err));
		dispatch(setCategorys(response.data));
	};

	useEffect(() => {
		fetchCategory();
	}, []);

	useEffect(() => {
		fetchProducts();
	}, []);

	return (
		<div className="main-content">
			<div className="wrap-banner">
				<div className="container">
					{/* slide show */}
					<Baner />
					{/* policy */}
					<Policy />
				</div>
			</div>
			{/* main */}
			<div id="wrapper-site">
				<div id="content-wrapper" className="full-width">
					<div id="main">
						<section className="page-home">
							{/* FEATURED CATEGORIES */}
							<ListDanhmuc />
							{/*FEATURED PRODUCTS */}
							<div className="featured-product">
								<div className="container">
									<div className="title-tab-content  text-center">
										<div className="title-product justify-content-between">
											<h2>SẢN PHẨM MỚI NHẤT</h2>
											<p>BẠN CÓ LỰA CHỌN SẢN PHẨM PHÙ HỢP CHO BẠN CHƯA </p>
										</div>
									</div>
									<div className="tab-content">
										<NewProduct />
										<div className="content-showmore mb-5 text-center has-showmore" style={{ clear: 'both', position: 'relative' }}>
											<button type="button" className="btn btn-default novShowMore" name="novShowMore" data-loading="Loading..." data-loadmore="Load More Products">
												<span>Load More Products</span>
											</button>
											<input type="hidden" defaultValue={0} className="count_showmore" />
										</div>
									</div>
								</div>
							</div>
						</section>
					</div>
				</div>
			</div>

		</div>
	)
}

export default Home;
