import { ActionTypes } from '../contants/action-type'
const initialState = {
    product: [
        {
            id: 1,
            title: "display",
            category: 'xin ca',
        }
    ],
}

const productReducer = (state, { type, payload }) => {
    switch (type) {
        case ActionTypes.SET_PRODUCTS:
            return state;
        default:
            return state;

    }
}

export default productReducer;
