import { combineReducers } from 'redux';
import { productReducer, selectedProductReducer, discountReducer } from './productReducer';
import addItem from './addItem'
import { categoryReducer, selectedCategoryReducer } from './categoryReducer';
const Reducer = combineReducers({
    addItem,
    allProduct: productReducer,
    product: selectedProductReducer,
    allcategoty: categoryReducer,
    spCategory: selectedCategoryReducer,
    allDiscount: discountReducer,
});

export default Reducer;
