import { ActionTypes } from '../contants/action-type';

const initialState = {
    caterogy: []
};
const map = {
    spcaterogy: []
}
const initialRooms = {
    roomsType: []
}

export const categoryReducer = (state = initialState, { type, payload }) => {
    switch (type) {
        case ActionTypes.SELECT_CATEGORY:
            return { ...state, caterogy: payload };
        default:
            return state;

    }
};

export const selectedCategoryReducer = (state = map, { type, payload }) => {
    switch (type) {
        case ActionTypes.SELECT_OF_CATEGORY:
            return { ...state, spcaterogy: payload };
        default:
            return state;
    }
}

export const selectedRoomTypesReducer = (state = roomsType, { type, payload }) => {
    switch (type) {
        case ActionTypes.SELECT_ROOM_TYPES:
            return { ...state, roomsType: payload };
        default:
            return state;
    }
}
