import React from 'react';
import { useSelector } from 'react-redux';

const ListDanhmuc = () => {
    const category = useSelector((state) => state.allcategoty.caterogy);

    console.log(category);
    const renderList = category.map((category) => {
        const { id, imageName,
            maLoai, tenLoai, urlAnhDaiDien, danhMucSanPham } = category;
        return (
            <div className="content-category" key={id}>
                <div className="content-img">
                    <a href="product-grid-sidebar-left.html">
                        <img className="img-fluid" src="img/product/27.jpg" alt="Side Table" title="Side Table" />
                    </a>
                </div>
                <div className="info-category">
                    <h3>
                        <a href="product-grid-sidebar-left.html">{tenLoai}</a>
                    </h3>
                    <p>Có {danhMucSanPham.length} sản phẩm </p>
                </div>
            </div>
        );
    })

    return (
        <div className="featured-category">
            <div className="container">
                <div className="tab-content text-center">
                    <div className="title-product">
                        <h2>FEATURED CATEGORIES</h2>
                        <p>LOREM IPSUM DOLOR SIT AMET CONSECTETUR</p>
                    </div>
                    <div className="featured owl-carousel owl-theme">

                        {renderList}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ListDanhmuc;
