import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';

const BaseURL = "https://localhost:44336/api/Products/";

const SearchProduct = () => {
    const searchProducts = useSelector((state) => state.listAllProduct.products);
    const [product, setProduct] = useState([])
    const [text, setText] = useState('')
    const [suggestions, setSuggestions] = useState([])
    useEffect(() => {
        const loadDataSearch = async () => {
            const response = await axios.get(BaseURL + "GetAllSanPham")
                .catch((err) => console.error(err))
            setProduct(response.data)
        }
        loadDataSearch();
    }, [])

    const headlineSearch = (text) => {
        let matches = []
        if (text.length > 0) {
            matches = product.filter(product => {
                const regex = new RegExp(`${text}`, "gi");
                return product.tenSP.match(regex);
            })
        }
        console.log("matches", matches)
        setSuggestions(matches)
        setText(text)

    }
    console.log(product)
    return (
        <>
            <div style={{
                width: '12.5pc', marginRight: '22.5pt',
            }}>
                <input type="text" placeholder="Search" className="ui-autocomplete-input"
                    onChange={e => headlineSearch(e.target.value)}
                    style={{
                        background: ' #e5e5e5',
                        borderRadius: '0.208333333in', width: '207px', height: '36px', border: 'none', padding: '0px 0px 0px 14px'
                    }}
                    value={text}
                />
            </div>
            <div>
                {suggestions && suggestions.map((suggestions, i) =>
                    <div key={i}>
                        <p >{suggestions.tenSP}</p>
                    </div>

                )}
            </div>
        </>

    );
};

export default SearchProduct;
