import './App.css';
import * as React from 'react';
import "react-toastify/dist/ReactToastify.css";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { ToastContainer } from "react-toastify";
import { Provider } from 'react-redux';
import Footer from './Components/Footer/Footer';
import Header from './Components/Header/Header';
import About from './Components/About/About';
import Checkout from './Components/Checkout/Checkout';
import Contact from './Components/Contact/Contact';
import DetailProduct from './Components/DetailProduct/DetailProduct';
import Erro404 from './Components/Erro404/Erro404';
import Product from './Components/Product/Product';
import Login from './Components/User/MainLogin';
import Register from './Components/User/MainRegister';
import store from './redux/store';
import Viewcart from './Components/Cart/Cart';
import DanhMucProduct from './Components/Product/DanhMucProduct'
import SuccessCheckout from './Components/Checkout/SuccessCheckout';

const Home = React.lazy(() => import('./Components/Home/Home'));

function App() {
  return (
    <Router>
      <Provider store={store}>
        <ToastContainer />
        <div id="home4">
          <Header />
          <Switch>
            <React.Suspense fallback={<p>loading</p>}>
              <Route exact path="/"> <Home /> </Route>
              <Route exact path="/Product"> <Product /> </Route>
              <Route exact path="/About"> <About /> </Route>
              <Route exact path="/Checkout"> <Checkout /> </Route>
              <Route exact path="/Contact"> <Contact /> </Route>
              <Route exact path="/DetailProduct/:id"> <DetailProduct /></Route>
              <Route exact path="/Viewcart"> <Viewcart /> </Route>
              <Route exact path="/Products/:id"> <DanhMucProduct /> </Route>
              {/* ------------------- */}
              <Route exact path="/Register"> <Register /> </Route>
              <Route exact path="/Login"> <Login /> </Route>
              <Route exact path="/Success"> <SuccessCheckout /> </Route>
              <Route> <Erro404 /> </Route>
            </React.Suspense>
          </Switch>
          <Footer />
        </div>
      </Provider>
    </Router>
  );
}

export default App;
