import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';

const BaseURL = "https://localhost:44336/api/Products/";

const SearchProduct = () => {
    const searchProducts = useSelector((state) => state.listAllProduct.products);
    const [nameProduct, setNameProduct] = useState([])
    const [productmax, setProductMax] = useState([])

    useEffect(() => {
        const loadData = async () => {
            const response = await axios.get(BaseURL + "GetAllSanPham")
                .catch((err) => console.error(err))
            setNameProduct(response.data)
        }
        loadData();
    })
    console.log(nameProduct)
    const headlineSearch = (text) => {
        let matches = nameProduct.filter((product) => {
            const regex = new RegExp(`${text}`, "gi");
            return product.tenSP.match(regex);
        })
        setProductMax(matches);
    }

    return (
        <div id="mobile_search_content">
            <form method="get" action="#">
                <input type="text"
                    autoComplete="off" name="s" placeholder="Search" value=" "
                    onchange={(e) => headlineSearch(e.target.value)}
                />
                <button type="submit">
                    <i className="fa fa-search" />
                </button>
            </form>
        </div>
    );
};

export default SearchProduct;
