import React, { useState, useEffect } from 'react';
import { useForm, Form } from '../../../src/hooks/useForm';
import { useSelector } from 'react-redux';
import { useHistory } from "react-router-dom";

const initialFValues = {
  idKhach: 0,
  hoTen: '',
  sdt: '',
  diaChi: '',
  email: '',
  userName: '',
  passWp: '',
}

const Register = (props) => {
  const { AddUser } = props;
  let history = useHistory();

  const validate = (fieldValues = values) => {
    let temp = { ...errors }
    if ('hoTen' in fieldValues)
      temp.hoTen = fieldValues.hoTen ? "" : "This field is required."
    if ('sdt' in fieldValues)
      temp.sdt = fieldValues.sdt ? "" : "This field is required."
    if ('diaChi' in fieldValues)
      temp.diaChi = fieldValues.diaChi ? "" : "This field is required."
    if ('email' in fieldValues)
      temp.email = fieldValues.email ? "" : "This field is required."
    setErrors({
      ...temp
    })
    if (fieldValues == values)
      return Object.values(temp).every(x => x == "")
  }

  const {
    values, setValues,
    errors, setErrors,
    handleInputChange,
    resetForm
  } = useForm(initialFValues, true, validate);

  const handleSubmit = e => {
    e.preventDefault()
    if (validate()) {
      const FormUser = new FormData()
      FormUser.append('idKhach', values.idKhach)
      FormUser.append('hoTen', values.hoTen)
      FormUser.append('diaChi', values.diaChi)
      FormUser.append('sdt', values.sdt)
      FormUser.append('email', values.email)
      FormUser.append('userName', values.userName)
      FormUser.append('passWp', values.passWp)
      AddUser(FormUser, resetForm);
      history.push("/Login");
    }
  }

  return (
    <div className="user-register blog">
      <div className="main-content">
        <div className="wrap-banner">
        </div>
        <div id="wrapper-site">
          <div className="container" style={{ maxWidth: '95%', margin: '0 auto', marginTop: '80px' }}>
            <div className="row">
              <div id="content-wrapper" className="col-xs-12 col-sm-12 col-md-12 col-lg-12 onecol">
                <div id="main">
                  <div id="content" className="page-content">
                    <div className="register-form text-center">
                      <h1 className="text-center title-page">Tạo tài khoản</h1>
                      <form onsubmit={handleSubmit} id="customer-form" className="js-customer-form">
                        <div>
                          <div className="form-group">
                            <div>
                              <input className="form-control"
                                name="hoTen" type="text"
                                placeholder="Họ và tên"
                                autoComplete="off"
                                value={values.hoTen}
                                onChange={handleInputChange}
                                error={errors.hoTen}
                              />
                            </div>
                          </div>
                          <div className="form-group">
                            <div>
                              <input className="form-control" name="sdt" type="text" placeholder="Số điện thoại"
                                autoComplete="off"
                                value={values.sdt}
                                onChange={handleInputChange}
                                error={errors.sdt}
                              />
                            </div>
                          </div>
                          <div className="form-group">
                            <div>
                              <input className="form-control" name="email" type="email" placeholder="Email"
                                autoComplete="off"
                              />
                            </div>
                          </div>
                          <div className="form-group">
                            <div>
                              <input className="form-control" name="UserName" type="text" placeholder="Tên tài khoản"
                                autoComplete="off" />
                            </div>
                          </div>
                          <div className="form-group">
                            <div>
                              <div className="input-group js-parent-focus">
                                <input className="form-control js-child-focus js-visible-password" name="password" type="password" placeholder="Password"
                                  autoComplete="off"
                                />
                              </div>
                            </div>
                          </div>
                          <textarea className="form-control js-child-focus js-visible"
                            placeholder="Địa chỉ"
                            name="diaChi" rows={5}
                            autoComplete="off"
                          />
                        </div>
                        <div className="clearfix">
                          <div>
                            <button className="btn btn-primary" data-link-action="sign-in" type="submit">
                              Tạo tài khoản
                            </button>
                          </div>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
