import React, { useState, useEffect } from 'react';
import { useForm, Form } from '../../../src/hooks/useForm';
const initialFValues = {
    idKhach: 0,
    ho: '',
    ten: '',
    sdt: '',
    diaChi: '',
    email: '',
    soLuong: 0,
    thanhTien: 0,
    idCheckout: 0,
    idSanPham: 0,
}

const FormCheckout = (props) => {
    const { addOrEdit } = props;
    const dataCart = JSON.parse(localStorage.getItem("cartItems"))
    const dataCarts = JSON.parse(localStorage.getItem("cart"))

    const validate = (fieldValues = values) => {
        let temp = { ...errors }
        if ('ho' in fieldValues)
            temp.ho = fieldValues.ho ? "" : "This field is required."
        setErrors({
            ...temp
        })
        if (fieldValues == values)
            return Object.values(temp).every(x => x == "")
    }

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useForm(initialFValues, validate, true);

    const handleSubmit = e => {
        e.preventDefault()
        if (validate()) {
            const formDataCheckout = new FormData()
            formDataCheckout.append('idKhach', values.idKhach)
            formDataCheckout.append('ho', values.ho)
            formDataCheckout.append('ten', values.ten)
            formDataCheckout.append('diaChi', values.diaChi)
            formDataCheckout.append('sdt', values.sdt)
            formDataCheckout.append('email', values.email)

            formDataCheckout.append('idSanPham', dataCart.idSanPham)
            if (dataCart != null) {
                for (const key of Object.keys(dataCart)) {
                    formDataCheckout.append('idSanPham', dataCart[key].id)
                    formDataCheckout.append('soLuong', dataCart[key].cartQuantity)
                }
            } else {
                formDataCheckout.append('idSanPham', dataCart[0].id)
            }
            formDataCheckout.append('thanhTien', dataCarts.cartTotalAmount)
            addOrEdit(formDataCheckout, resetForm);
        }
    }

    return (
        <form id="customer-form" onSubmit={handleSubmit} className="js-customer-form" method="post">
            <div>
                <input type="hidden" name="id_customer" defaultValue />
                <div className="form-group row">
                    <input
                        className="form-control"
                        name="ho" type="text"
                        placeholder="Họ"
                        value={values.ho}
                        onChange={handleInputChange}
                        error={errors.ho}
                    />
                </div>
                <div className="form-group row">
                    <input
                        className="form-control"
                        name="tên" type="text"
                        placeholder="Tên"
                        value={values.ten}
                        onChange={handleInputChange}
                        error={errors.ten}
                    />
                </div>
                <div className="form-group row">
                    <input
                        className="form-control"
                        name="sdt" type="text"
                        placeholder="sdt"
                        value={values.sdt}
                        onChange={handleInputChange}
                        error={errors.sdt}
                    />
                </div>
                <div className="form-group row">
                    <input
                        className="form-control"
                        name="email" type="text"
                        placeholder="email"
                        value={values.email}
                        onChange={handleInputChange}
                        error={errors.email}
                    />
                </div>
            </div>
            <div className="clearfix">
                <div className="row">
                    <input type="hidden" name="submitCreate" />
                    <button className="continue btn btn-primary pull-xs-right" name="Đặt hàng" type="submit">
                        Continue
                    </button>
                </div>
            </div>
        </form>
    );
};

export default FormCheckout;
