import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

const ListDanhmuc = () => {
    const category = useSelector((state) => state.allcategoty.caterogy);

    console.log(category);
    const renderList = category.map((category) => {
        const { id, tenLoai, urlAnhDaiDien, danhMucSanPham } = category;
        return (
            <>
                <div className="owl-item cloned" style={{ width: '233.8px', marginRight: '30px' }}>



                </div>
                <div className="owl-item" style={{ width: '233.8px', marginRight: '30px' }}>


                </div>
                <div className="owl-item active" style={{ width: '233.8px', marginRight: '30px' }}>


                </div>
            </>
        );
    })
    return (
        <>
            <div className="owl-stage-outer">
                <div className="owl-stage" style={{ transform: 'translate3d(-1846px, 0px, 0px)', transition: 'all 0.25s ease 0s', width: '4485px' }}>
                    <div className="owl-item cloned" style={{ width: '233.8px', marginRight: '30px' }}>



                    </div>
                    <div className="owl-item" style={{ width: '233.8px', marginRight: '30px' }}>


                    </div>
                    <div className="owl-item active" style={{ width: '233.8px', marginRight: '30px' }}>


                    </div>
                </div>
            </div>

        </>
    )

    return (
        <div className="featured-category">
            <div className="container">
                <div className="tab-content text-center">
                    <div className="title-product">
                        <h2>DANH MỤC SẢN PHẨM</h2>
                        <p>BẠN THÍCH THỂ LOẠI NÀO !</p>
                    </div>
                    <div className="featured owl-carousel owl-theme">
                        <div className="owl-stage-outer">
                            <div className="owl-stage" style={{ transform: 'translate3d(-1846px, 0px, 0px)', transition: 'all 0.25s ease 0s', width: '4485px' }}>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ListDanhmuc;
