import React, { useState, useEffect } from 'react'
import { useHistory } from "react-router-dom";
import { useForm, Form } from '../../hooks/useForm';

const initialFValues = {
  userName: '',
  passWp: '',
}

const Login = (props) => {
  const { UserLogin } = props;
  const history = useHistory();

  const validate = (fieldValues = values) => {
    let temp = { ...errors }
    if ('userName' in fieldValues)
      temp.userName = fieldValues.userName ? "" : "This field is required."
    if ('passWp' in fieldValues)
      temp.passWp = fieldValues.passWp ? "" : "This field is required."
    setErrors({
      ...temp
    })
    if (fieldValues == values)
      return Object.values(temp).every(x => x == "")
  }

  const {
    values, setValues,
    errors, setErrors,
    handleInputChange,
    resetForm
  } = useForm(initialFValues, true, validate);

  const handleSubmitUser = e => {
    e.preventDefault()
    if (validate()) {
      const FormUser = new FormData()
      FormUser.append('userName', values.userName)
      FormUser.append('passWp', values.passWp)
      UserLogin(FormUser, resetForm);
      history.push("/")
    }
  }

  useEffect(() => {
    if (localStorage.getItem('info_user')) {
      history.push("/")
    }
  })

  return (
    <form id="customer-form" >
      <div>
        <input type="hidden" name="back" defaultValue="my-account" />
        <div className="form-group no-gutters">
          <input className="form-control" name="email" type="email" placeholder=" Email" />
        </div>
        <div className="form-group no-gutters">
          <div className="input-group js-parent-focus">
            <input className="form-control js-child-focus js-visible-password" name="userName" type="password"
              value={values.hoTen}
              onChange={handleInputChange}
              error={errors.hoTen}
              placeholder="Nhập mật khẩu" />
          </div>
        </div>
        <div className="no-gutters text-center">
          <div className="forgot-password">
            <a href="user-reset-password.html" rel="nofollow">
              Forgot your password?
            </a>
          </div>
        </div>
      </div>
      <div className="clearfix">
        <div className="text-center no-gutters">
          <input type="hidden" name="submitLogin" defaultValue={1} />
          <button className="btn btn-primary" data-link-action="sign-in" type="submit">
            Sign in
          </button>
        </div>
      </div>
    </form>
  );
};

export default Login;
