import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

const responsive = {
    superLargeDesktop: {
        // the naming can be any, depends on you.
        breakpoint: { max: 4000, min: 3000 },
        items: 5
    },
    desktop: {
        breakpoint: { max: 3000, min: 1024 },
        items: 3
    },
    tablet: {
        breakpoint: { max: 1024, min: 464 },
        items: 2
    },
    mobile: {
        breakpoint: { max: 464, min: 0 },
        items: 1
    }
};

function CustomRightArrow({ onClick }) {
    return (
        <button
            onClick={handleClick}
            aria-label="Go to next slide"
            className="react-multiple-carousel__arrow react-multiple-carousel__arrow--right"
        />
    );
}

function CustomLeftArrow({ onClick }) {

    return (
        <button
            onClick={handleClick}
            aria-label="Go to previous slide"
            className="react-multiple-carousel__arrow react-multiple-carousel__arrow--left"
        />
    );
}

const ButtonGroup = ({ next, previous }) => {
    return (
        <div className="carousel-button-group">
            <CustomLeftArrow
                onClick={() => previous()}
            />
            <CustomRightArrow onClick={() => next()} />
        </div>
    );
};

const ListDanhmuc = () => {
    const category = useSelector((state) => state.allcategoty.caterogy);

    console.log(category);
    const renderList = category.map((category) => {
        const { id, tenLoai, urlAnhDaiDien, danhMucSanPham } = category;
        return (
            <div className="content-category" keys={id}>
                <div className="content-img" style={{ width: '233.8px', height: '233.80px' }}>
                    <Link to={`/Products/${id}`}>
                        <img className="img-fluid"
                            style={{ width: '233.8px', height: '233.80px', objectFit: 'fill' }}
                            src={urlAnhDaiDien} alt="Side Table" title="Side Table" />
                    </Link>
                </div>
                <div className="info-category">
                    <h3>
                        <Link to={`/Products/${id}`}>{tenLoai}</Link>
                    </h3>
                    <p>Có {danhMucSanPham.length} sản phẩm </p>
                </div>
            </div>
        );
    })



    return (
        <div className="featured-category">
            <div className="container">
                <div className="tab-content text-center">
                    <div className="title-product">
                        <h2>DANH MỤC SẢN PHẨM</h2>
                        <p>BẠN THÍCH THỂ LOẠI NÀO !</p>
                    </div>
                    <Carousel
                        customLeftArrow={<CustomLeftArrow />}
                        customRightArrow={<CustomRightArrow />}
                        renderButtonGroupOutside={true}
                        customButtonGroup={<ButtonGroup />}
                        responsive={responsive}
                        arrows={false}
                        className="featured">
                        {renderList}
                    </Carousel>
                </div>
            </div>
        </div>
    );
};

export default ListDanhmuc;
