import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';

const BaseURL = "https://localhost:44336/api/Products/";

const SearchProduct = () => {
    const searchProducts = useSelector((state) => state.listAllProduct.products);
    const [product, setProduct] = useState([])
    const [text, setText] = useState('')

    useEffect(() => {
        const loadDataSearch = async () => {
            const response = await axios.get(BaseURL + "GetAllSanPham")
                .catch((err) => console.error(err))
            setProduct(response.data)
            console.log(response.data)
        }
        loadDataSearch();
    }, [])
    const headlineSearch = (text) => {
        let matches = []
        if (text.length > 0) {
            matches = product.filter(urs => {
                const regex = new RegExp(`${text}`, "gi");

            })
        }
        setText(text)
    }
    console.log(product.tenSP)
    return (
        <div id="mobile_search_content">

            <input type="text"
                autoComplete="off" placeholder="Search"
                onchange={(e) => headlineSearch(e.target.value)}
                value={text}
            />
            {/* <button type="submit">
                <i className="fa fa-search" />
            </button> */}

        </div>
    );
};

export default SearchProduct;
