import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import NewProduct from './NewProduct';
import { setProducts } from '../../redux/actions/productActions';
import { setCategorys } from '../../redux/actions/categoryActions';
import ListDanhmuc from './ListDanhmuc';
import Baner from './Baner';

function Home() {
	const product = useSelector((state) => state);

	const dispatch = useDispatch();

	const fetchProducts = async () => {
		const response = await axios.get("https://localhost:44336/api/Home/SanPhamMoiNhat")
			.catch((err) => console.log(err));
		dispatch(setProducts(response.data));
	};

	const fetchCategory = async () => {
		const response = await axios.get("https://localhost:44336/api/Home/ListDanhMucSp")
			.catch((err) => console.log(err));
		dispatch(setCategorys(response.data));
	};

	useEffect(() => {
		fetchCategory();
	}, []);

	useEffect(() => {
		fetchProducts();
	}, []);

	return (
		<div className="main-content">
			<div className="wrap-banner">
				<div className="container">
					{/* slide show */}
					<Baner />
					{/* policy */}
					<div className="row">
						<div className="policy-home col-lg-12">
							<div className="row">
								<div className="block col-lg-4 col-md-4 col-xs-12 d-flex justify-content-center">
									<div className="block-content">
										<div className="policy-item">
											<div className="policy-content iconpolicy1">
												<img src="img/home/home1-policy.png" alt="img" />
												<div className="policy-name mb-5">FREE DELIVERY FROM $ 250</div>
												<div className="policy-des">Lorem ipsum dolor Sed ut perspiciatis unde omnis</div>
											</div>
										</div>
									</div>
								</div>
								<div className="block tiva-html col-lg-4 col-md-4 col-xs-12 d-flex  justify-content-center">
									<div className="block-content">
										<div className="policy-item">
											<div className="policy-content iconpolicy2">
												<img src="img/home/home1-policy2.png" alt="img" />
												<div className="policy-name mb-5">FREE INSTALLATION</div>
												<div className="policy-des">Lorem ipsum dolor Sed ut perspiciatis unde omnis</div>
											</div>
										</div>
									</div>
								</div>
								<div className="block tiva-html col-lg-4 col-md-4 col-xs-12 d-flex justify-content-center">
									<div className="block-content">
										<div className="policy-item">
											<div className="policy-content iconpolicy3">
												<img src="img/home/home1-policy3.png" alt="img" />
												<div className="policy-name mb-5">MONEY BACK GUARANTEED</div>
												<div className="policy-des">Lorem ipsum dolor Sed ut perspiciatis unde omnis</div>
											</div>
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>
				</div>
			</div>
			{/* main */}
			<div id="wrapper-site">
				<div id="content-wrapper" className="full-width">
					<div id="main">
						<section className="page-home">
							{/* FEATURED CATEGORIES */}
							<ListDanhmuc />
							{/*FEATURED PRODUCTS */}
							<div className="featured-product">
								<div className="container">
									<div className="title-tab-content  text-center">
										<div className="title-product justify-content-between">
											<h2>SẢN PHẨM MỚI NHẤT</h2>
											<p>BẠN CÓ LỰA CHỌN SẢN PHẨM PHÙ HỢP CHO BẠN CHƯA </p>
										</div>
									</div>
									<div className="tab-content">
										<NewProduct />
										<div className="content-showmore mb-5 text-center has-showmore" style={{ clear: 'both', position: 'relative' }}>
											<button type="button" className="btn btn-default novShowMore" name="novShowMore" data-loading="Loading..." data-loadmore="Load More Products">
												<span>Load More Products</span>
											</button>
											<input type="hidden" defaultValue={0} className="count_showmore" />
										</div>
									</div>
								</div>
							</div>
						</section>
					</div>
				</div>
			</div>

		</div>
	)
}

export default Home;
