import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';

const BaseURL = "https://localhost:44336/api/Products/";

const SearchProduct = () => {
    const searchProducts = useSelector((state) => state.listAllProduct.products);
    console.log(searchProducts)
    const [nameProduct, setNameProduct] = useState([])
    const [productmax, setProductMax] = useState([])

    useEffect(() => {
        const loadData = async () => {
            const response = await axios.get(BaseURL + "GetAllSanPham")
                .catch((err) => console.error(err))
            console.log(response)
        }
    })
    return (
        <div id="mobile_search_content">
            <form method="get" action="#">
                <input type="text"
                    autoComplete="off" name="s" defaultValue placeholder="Search" value=" " />
                <button type="submit">
                    <i className="fa fa-search" />
                </button>
            </form>
        </div>
    );
};

export default SearchProduct;
