import { ActionTypes } from '../contants/action-type';

export const setProducts = (product) => {
    return {
        type: ActionTypes.SET_PRODUCTS,
        payload: product,
    }
}

export const selectedProduct = (product) => {
    return {
        type: ActionTypes.SELECTED_PRODUCT,
        payload: product,
    }
}
